import React from 'react'

const App = () => {
	const course = {
		name: 'Half Stack application development',
		parts: [
			{
			name: 'Fundamentals of React',
			exercises: 10
			},
			{
			name: 'Using props to pass data',
			exercises: 7
			},
			{
			name: 'State of a component',
			exercises: 14
			}
		]
	}

	return (
		<div>
			<Header course={course.name} />
			<Content parts={course.parts}/>
			<Total parts={course.parts}/>
		</div>
		)
	}

	const Header = (props) => {
		return (
			<div>
				<h1>{props.course}</h1>
			</div>
		)
	}

	const Part = (props) => {
		return (
			<>
				{props.part} {props.exercises}
			</>
		)
	}

	const Content = (props) => {
		return (
		<div>
			<p>
				<Part parts={props.parts[0].name} exercises={props.parts[0].name} />
				<Part parts={props.parts[1].name} exercises={props.parts[1].name} />
				<Part parts={props.parts[2].name} exercises={props.parts[2].name} />
			</p>
		</div>
		)
	}

	const Total = (props) => {
		return (
			<div>
				<p>Number of exercises {props.parts[0].exercises + props.parts[1].exercises + props.parts[2].exercises}</p>
			</div>
		)
	}

  export default App
